    let answer;

    const answerRef = useRef();
    if(difficulty === 'medium'){
        setDifficultyInteger = 15
    }else if(difficulty === 'hard'){
        setDifficultyInteger = 20
    }else{
        setDifficultyInteger = 10
    }

    if(mathType === 'subtraction'){
        setMathSign = '-'
    }else if(mathType === 'multiplication'){
        setMathSign = 'x'
